                edited ? ( 
                    <input 
                        type="text" 
                        className="todoapp__item-edit-input" 
                        value={newText} 
                        ref={editInputRef} 
                        onChange={onChangeEditInput} 
                    /> 
                ) : ( 
                   <span 
                        className={`todoapp__item-ctx ${ 
                            todoItem.checked ? 'todoapp__item-ctx-checked' : '' 
